    57â†’// ìŠ¤í¬ë¡¤ ì‹œë®¬ë ˆì´ì…˜
    58â†’const smoothScroll = async (page: Page, distance: number): Promise<void> => {
    59â†’  await page.evaluate(async (scrollDistance) => {
    60â†’    await new Promise<void>((resolve) => {
    61â†’      let totalScrolled = 0;
    62â†’      const step = 100;
    63â†’      const timer = setInterval(() => {
    64â†’        window.scrollBy(0, step);
    65â†’        totalScrolled += step;
    66â†’        if (totalScrolled >= scrollDistance) {
    67â†’          clearInterval(timer);
    68â†’          resolve();
    69â†’        }
    70â†’      }, 50);
    71â†’    });
    72â†’  }, distance);
    73â†’};
