   284â†’// ë¦¬ë·° ëª©ë¡ ì¶”ì¶œ (ì˜¬ë¦¬ë¸Œì˜ React êµ¬ì¡°)
   285â†’const extractReviews = async (page: Page, maxPages: number = 5): Promise<Review[]> => {
   286â†’  const reviews: Review[] = [];
   287â†’
   288â†’  console.log('ğŸ“– ë¦¬ë·° ìˆ˜ì§‘ ì¤‘...');
   289â†’
   290â†’  // íƒ€ê²Ÿ ë¦¬ë·° ìˆ˜
   291â†’  const targetReviews = maxPages * 20;
   292â†’  let noNewDataCount = 0;
   293â†’
   294â†’  for (let attempt = 0; attempt < maxPages * 5 && reviews.length < targetReviews && noNewDataCount < 3; attempt++) {
   295â†’    // page.evaluateë¡œ ì§ì ‘ DOMì—ì„œ ë¦¬ë·° ë°ì´í„° ì¶”ì¶œ
   296â†’    const extractedReviews = await page.evaluate(() => {
   297â†’      const reviewData: any[] = [];
   298â†’
   299â†’      // ëª¨ë“  ìš”ì†Œ ì¤‘ ë¦¬ë·°ì²˜ëŸ¼ ë³´ì´ëŠ” ê²ƒ ì°¾ê¸°
   300â†’      // ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° íŠ¹ì§•: ë³„ì  ì´ë¯¸ì§€, ë‚ ì§œ, ê¸´ í…ìŠ¤íŠ¸
   301â†’      document.querySelectorAll('*').forEach(el => {
   302â†’        const text = el.textContent || '';
   303â†’        const html = el.innerHTML || '';
   304â†’
   305â†’        // ë¦¬ë·° íŠ¹ì§• í™•ì¸
   306â†’        const hasDate = /\d{4}\.\d{2}\.\d{2}/.test(text);
   307â†’        const hasRating = text.includes('ì ') || html.includes('star') || html.includes('rating');
   308â†’        const isReasonableLength = text.length > 30 && text.length < 3000;
   309â†’        const className = (el.className || '').toString().toLowerCase();
   310â†’
   311â†’        // ë¦¬ë·° ì»¨í…Œì´ë„ˆë¡œ ë³´ì´ëŠ” ìš”ì†Œ
   312â†’        if (hasDate && isReasonableLength && !el.querySelector('article, section, main')) {
   313â†’          // ì´ë¯¸ ì¶”ì¶œí•œ ë‚´ìš©ê³¼ ì¤‘ë³µ í™•ì¸
   314â†’          const isDup = reviewData.some(r => text.includes(r.content?.substring(0, 50)));
   315â†’          if (isDup) return;
   316â†’
   317â†’          // ë‚ ì§œ ì¶”ì¶œ
   318â†’          const dateMatch = text.match(/(\d{4}\.\d{2}\.\d{2})/);
   319â†’          const date = dateMatch ? dateMatch[1] : '';
   320â†’
   321â†’          // í…ìŠ¤íŠ¸ ë¼ì¸ ë¶„ë¦¬
   322â†’          const lines = text.split('\n')
   323â†’            .map(l => l.trim())
   324â†’            .filter(l => l.length > 0);
   325â†’
   326â†’          // ë¦¬ë·° ë‚´ìš© ì°¾ê¸° (ê°€ì¥ ê¸´ ë¼ì¸, ë‹¨ ë‚ ì§œ/ë³„ì  ì œì™¸)
   327â†’          let content = '';
   328â†’          for (const line of lines) {
   329â†’            if (line.length > content.length &&
   330â†’                line.length > 20 &&
   331â†’                !line.match(/^\d{4}\.\d{2}\.\d{2}/) &&
   332â†’                !line.includes('ë„ì›€') &&
   333â†’                !line.includes('ì‹ ê³ ')) {
   334â†’              content = line;
   335â†’            }
   336â†’          }
   337â†’
   338â†’          // ì‘ì„±ì ì°¾ê¸° (ë³´í†µ ì§§ì€ ë¬¸ìì—´)
   339â†’          let reviewer = 'ìµëª…';
   340â†’          for (const line of lines) {
   341â†’            if (line.length > 2 && line.length < 20 && !line.match(/\d{4}\.\d{2}/) && line !== content) {
   342â†’              reviewer = line;
   343â†’              break;
   344â†’            }
   345â†’          }
   346â†’
   347â†’          // ë³„ì  ì¶”ì¶œ
   348â†’          let rating = 5;
   349â†’          const ratingMatch = text.match(/(\d)ì /);
   350â†’          if (ratingMatch) rating = parseInt(ratingMatch[1]);
   351â†’
   352â†’          if (content.length > 20) {
   353â†’            reviewData.push({
   354â†’              reviewer: reviewer.substring(0, 30),
   355â†’              rating,
   356â†’              date,
   357â†’              content: content.substring(0, 1000)
   358â†’            });
   359â†’          }
   360â†’        }
   361â†’      });
   362â†’
   363â†’      // ì¤‘ë³µ ì œê±°
   364â†’      const unique: any[] = [];
   365â†’      reviewData.forEach(r => {
   366â†’        if (!unique.some(u => u.content === r.content)) {
   367â†’          unique.push(r);
   368â†’        }
   369â†’      });
   370â†’
   371â†’      return unique;
   372â†’    });
   373â†’
   374â†’    const prevCount = reviews.length;
   375â†’
   376â†’    // ì¤‘ë³µ ì œê±°í•˜ë©´ì„œ ì¶”ê°€
   377â†’    for (const review of extractedReviews) {
   378â†’      const isDuplicate = reviews.some(r =>
   379â†’        r.content === review.content ||
   380â†’        (r.content.length > 50 && review.content.includes(r.content.substring(0, 50)))
   381â†’      );
   382â†’      if (!isDuplicate && review.content && review.content.length > 20) {
   383â†’        reviews.push(review);
   384â†’      }
   385â†’    }
   386â†’
   387â†’    if (reviews.length === prevCount) {
   388â†’      noNewDataCount++;
   389â†’    } else {
   390â†’      noNewDataCount = 0;
   391â†’      console.log(`   ìˆ˜ì§‘: ${reviews.length}ê°œ`);
   392â†’    }
   393â†’
   394â†’    // ìŠ¤í¬ë¡¤ ë‹¤ìš´
   395â†’    await page.evaluate(() => window.scrollBy(0, 500));
   396â†’    await randomDelay(1000, 2000);
   397â†’  }
   398â†’
   399â†’  console.log(`ğŸ“„ ìˆ˜ì§‘ ì™„ë£Œ: ì´ ${reviews.length}ê°œ`);
   400â†’  return reviews;
