/**
 * ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° í¬ë¡¤ëŸ¬ (ê°œì¸ ë¶„ì„ìš©)
 * Playwright + Stealth í”ŒëŸ¬ê·¸ì¸ìœ¼ë¡œ ë´‡ íƒì§€ ìš°íšŒ
 *
 * ì‚¬ìš©ë²•:
 * npx ts-node scripts/oliveyoung-review-crawler.ts <ìƒí’ˆURL>
 *
 * ì˜ˆì‹œ:
 * npx ts-node scripts/oliveyoung-review-crawler.ts "https://www.oliveyoung.co.kr/store/goods/getGoodsDetail.do?goodsNo=A000000235842"
 */

import { chromium, Browser, Page } from 'playwright';
import * as fs from 'fs';
import * as path from 'path';

// ë¦¬ë·° ë°ì´í„° íƒ€ì…
interface Review {
  reviewer: string;
  rating: number;
  date: string;
  content: string;
  skinType?: string;
  skinTone?: string;
  helpfulCount?: number;
  photoUrls?: string[];
}

interface CrawlResult {
  productName: string;
  productUrl: string;
  totalReviews: number;
  averageRating: number;
  reviews: Review[];
  crawledAt: string;
}

// ëœë¤ ë”œë ˆì´ (ë´‡ íƒì§€ ìš°íšŒ)
const randomDelay = (min: number, max: number): Promise<void> => {
  const delay = Math.floor(Math.random() * (max - min + 1)) + min;
  return new Promise(resolve => setTimeout(resolve, delay));
};

// ë§ˆìš°ìŠ¤ ì›€ì§ì„ ì‹œë®¬ë ˆì´ì…˜
const simulateHumanBehavior = async (page: Page): Promise<void> => {
  const viewport = page.viewportSize();
  if (!viewport) return;

  // ëœë¤ ìœ„ì¹˜ë¡œ ë§ˆìš°ìŠ¤ ì´ë™
  for (let i = 0; i < 3; i++) {
    const x = Math.floor(Math.random() * viewport.width);
    const y = Math.floor(Math.random() * viewport.height);
    await page.mouse.move(x, y, { steps: 10 });
    await randomDelay(100, 300);
  }
};

// ìŠ¤í¬ë¡¤ ì‹œë®¬ë ˆì´ì…˜
const smoothScroll = async (page: Page, distance: number): Promise<void> => {
  await page.evaluate(async (scrollDistance) => {
    await new Promise<void>((resolve) => {
      let totalScrolled = 0;
      const step = 100;
      const timer = setInterval(() => {
        window.scrollBy(0, step);
        totalScrolled += step;
        if (totalScrolled >= scrollDistance) {
          clearInterval(timer);
          resolve();
        }
      }, 50);
    });
  }, distance);
};

// ë¸Œë¼ìš°ì € ì„¤ì • (Stealth ëª¨ë“œ)
const launchBrowser = async (): Promise<Browser> => {
  return chromium.launch({
    headless: false, // headed ëª¨ë“œ (ë´‡ íƒì§€ ìš°íšŒì— ì¤‘ìš”)
    args: [
      '--disable-blink-features=AutomationControlled',
      '--disable-features=IsolateOrigins,site-per-process',
      '--no-sandbox',
      '--disable-setuid-sandbox',
      '--disable-dev-shm-usage',
      '--disable-accelerated-2d-canvas',
      '--disable-gpu',
      '--window-size=1920,1080',
      '--start-maximized',
    ],
  });
};

// í˜ì´ì§€ ì„¤ì • (í•‘ê±°í”„ë¦°íŠ¸ ìœ„ì¥)
const setupPage = async (browser: Browser): Promise<Page> => {
  const context = await browser.newContext({
    viewport: { width: 1920, height: 1080 },
    userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
    locale: 'ko-KR',
    timezoneId: 'Asia/Seoul',
    geolocation: { latitude: 37.5665, longitude: 126.9780 }, // ì„œìš¸
    permissions: ['geolocation'],
  });

  const page = await context.newPage();

  // WebDriver ê°ì§€ ìš°íšŒ (ë¬¸ìì—´ë¡œ ì „ë‹¬í•˜ì—¬ ë¸Œë¼ìš°ì € ì»¨í…ìŠ¤íŠ¸ì—ì„œ ì‹¤í–‰)
  await page.addInitScript(`
    // navigator.webdriver ìˆ¨ê¸°ê¸°
    Object.defineProperty(navigator, 'webdriver', {
      get: () => undefined,
    });

    // chrome runtime ìœ„ì¥
    window.chrome = {
      runtime: {},
    };

    // permissions ìœ„ì¥
    const originalQuery = window.navigator.permissions.query.bind(window.navigator.permissions);
    window.navigator.permissions.query = (parameters) =>
      parameters.name === 'notifications'
        ? Promise.resolve({ state: 'denied', onchange: null })
        : originalQuery(parameters);

    // plugins ìœ„ì¥
    Object.defineProperty(navigator, 'plugins', {
      get: () => [1, 2, 3, 4, 5],
    });

    // languages ìœ„ì¥
    Object.defineProperty(navigator, 'languages', {
      get: () => ['ko-KR', 'ko', 'en-US', 'en'],
    });
  `);

  return page;
};

// ë¦¬ë·° íƒ­ í´ë¦­ ë° ë¡œë”©
const navigateToReviews = async (page: Page, url: string): Promise<void> => {
  console.log('ğŸ“¦ ìƒí’ˆ í˜ì´ì§€ ë¡œë”© ì¤‘...');
  await page.goto(url, { waitUntil: 'domcontentloaded', timeout: 60000 });
  await randomDelay(3000, 5000);

  // ë””ë²„ê·¸: í˜„ì¬ URLê³¼ íƒ€ì´í‹€ ì¶œë ¥
  console.log('ğŸ“ í˜„ì¬ URL:', page.url());
  console.log('ğŸ“ í˜ì´ì§€ íƒ€ì´í‹€:', await page.title());

  // ì¸ê°„ì²˜ëŸ¼ í–‰ë™
  await simulateHumanBehavior(page);

  // ë¦¬ë·° ì„¹ì…˜ê¹Œì§€ ìŠ¤í¬ë¡¤
  console.log('ğŸ’¬ ë¦¬ë·° ì„¹ì…˜ìœ¼ë¡œ ìŠ¤í¬ë¡¤ ì¤‘...');
  await smoothScroll(page, 1500);
  await randomDelay(2000, 3000);

  // ë¦¬ë·° íƒ­ í´ë¦­ ì‹œë„ (ì—¬ëŸ¬ ì„ íƒì)
  const reviewTabSelectors = [
    'a[href="#reviewInfo"]',
    '.goods_reputation a',
    '[data-tab="review"]',
    'a:has-text("ë¦¬ë·°")',
    '.tabMenu a:has-text("ë¦¬ë·°")',
    '#reviewInfo',
    '.review-tab',
  ];

  for (const selector of reviewTabSelectors) {
    try {
      const tab = page.locator(selector).first();
      if (await tab.count() > 0 && await tab.isVisible()) {
        console.log(`   ë¦¬ë·° íƒ­ í´ë¦­: ${selector}`);
        await tab.click();
        await randomDelay(2000, 3000);
        break;
      }
    } catch (e) {
      // ë‹¤ìŒ ì„ íƒì ì‹œë„
    }
  }

  // ë¦¬ë·° ë¡œë”© ëŒ€ê¸° (íƒ€ì„ì•„ì›ƒ ì™„í™”)
  try {
    await page.waitForLoadState('networkidle', { timeout: 10000 });
  } catch (e) {
    console.log('   ë„¤íŠ¸ì›Œí¬ ëŒ€ê¸° íƒ€ì„ì•„ì›ƒ, ê³„ì† ì§„í–‰...');
  }
  await randomDelay(1000, 2000);

  // URLì— tab=reviewê°€ ìˆìœ¼ë©´ ì´ë¯¸ ë¦¬ë·° íƒ­ì´ ì—´ë¦° ìƒíƒœ
  // ë¦¬ë·°&ì„±ë¶„ íƒ­ í´ë¦­ (ìƒë‹¨ íƒ­ ë©”ë‰´)
  console.log('ğŸ” ë¦¬ë·°&ì„±ë¶„ íƒ­ í´ë¦­ ì‹œë„...');
  try {
    // íƒ­ ë©”ë‰´ì—ì„œ "ë¦¬ë·°&ì„±ë¶„" ë˜ëŠ” "ë¦¬ë·°" íƒ­ ì°¾ê¸°
    const tabSelectors = [
      'a:has-text("ë¦¬ë·°&ì„±ë¶„")',
      'a:has-text("ë¦¬ë·°")',
      '[class*="tabMenu"] a:nth-child(2)',
      '.tabMenu a[href*="review"]',
    ];

    for (const selector of tabSelectors) {
      const tab = page.locator(selector).first();
      if (await tab.count() > 0 && await tab.isVisible()) {
        console.log(`   íƒ­ í´ë¦­: ${selector}`);
        await tab.click();
        await randomDelay(2000, 3000);
        break;
      }
    }
  } catch (e) {
    console.log('   íƒ­ í´ë¦­ ì‹¤íŒ¨, ê³„ì† ì§„í–‰...');
  }

  // ì¶”ê°€ ìŠ¤í¬ë¡¤ë¡œ ë¦¬ë·° ì„¹ì…˜ í™•ë³´
  await smoothScroll(page, 800);
  await randomDelay(2000, 3000);

  // ìŠ¤í¬ë¦°ìƒ· ì €ì¥ (ë””ë²„ê·¸ìš©)
  await page.screenshot({ path: './output/debug-review-section.png', fullPage: false });
  console.log('ğŸ“¸ ìŠ¤í¬ë¦°ìƒ· ì €ì¥: ./output/debug-review-section.png');

  // HTML êµ¬ì¡° ë¶„ì„ì„ ìœ„í•´ ë¦¬ë·° ì˜ì—­ HTML ì¶œë ¥
  const reviewHtml = await page.evaluate(() => {
    // ë¦¬ë·° ë¦¬ìŠ¤íŠ¸ ì˜ì—­ ì°¾ê¸°
    const reviewList = document.querySelector('[class*="ReviewList"], [class*="review-list"], [class*="Modal"]');
    if (reviewList) return reviewList.innerHTML.substring(0, 3000);

    // ì „ì²´ bodyì—ì„œ ë¦¬ë·° ê´€ë ¨ ìš”ì†Œ ì°¾ê¸°
    const allReviews = document.querySelectorAll('[class*="review"]');
    let html = '';
    allReviews.forEach((el, i) => {
      if (i < 5) html += el.className + ': ' + el.innerHTML.substring(0, 200) + '\n---\n';
    });
    return html || 'No review section found';
  });
  console.log('ğŸ“„ ë¦¬ë·° ì˜ì—­ HTML ìƒ˜í”Œ:', reviewHtml.substring(0, 800));
};

// ìƒí’ˆ ì •ë³´ ì¶”ì¶œ
const extractProductInfo = async (page: Page): Promise<{ name: string; avgRating: number; totalReviews: number }> => {
  console.log('ğŸ“Š ìƒí’ˆ ì •ë³´ ì¶”ì¶œ ì¤‘...');

  // ë” ë„“ì€ ì„ íƒìë¡œ ìƒí’ˆëª… ì°¾ê¸°
  let name = 'ìƒí’ˆëª… ì—†ìŒ';
  const nameSelectors = ['.prd_name', '.goods_name', 'h1.name', '.product-name', '[class*="prdName"]', '[class*="goodsName"]', 'h1', '.tit'];
  for (const selector of nameSelectors) {
    try {
      const el = page.locator(selector).first();
      if (await el.count() > 0) {
        const text = await el.textContent({ timeout: 3000 });
        if (text && text.trim().length > 0) {
          name = text.trim();
          console.log(`   ìƒí’ˆëª… ì„ íƒì: ${selector}`);
          break;
        }
      }
    } catch (e) {
      // ë‹¤ìŒ ì„ íƒì ì‹œë„
    }
  }

  // í‰ê·  ë³„ì 
  let avgRating = 0;
  try {
    const ratingText = await page.locator('.score_area .point, .review_score .score, [class*="rating"], [class*="score"]').first().textContent({ timeout: 3000 });
    avgRating = parseFloat(ratingText?.replace(/[^0-9.]/g, '') || '0');
  } catch (e) {
    console.log('   ë³„ì  ì¶”ì¶œ ì‹¤íŒ¨');
  }

  // ì´ ë¦¬ë·° ìˆ˜
  let totalReviews = 0;
  try {
    const reviewCountText = await page.locator('.review_count, .count_num, .review_total, [class*="reviewCnt"]').first().textContent({ timeout: 3000 });
    totalReviews = parseInt(reviewCountText?.replace(/[^0-9]/g, '') || '0');
  } catch (e) {
    console.log('   ë¦¬ë·° ìˆ˜ ì¶”ì¶œ ì‹¤íŒ¨');
  }

  return { name, avgRating, totalReviews };
