// ë¦¬ë·° ëª©ë¡ ì¶”ì¶œ (ì˜¬ë¦¬ë¸Œì˜ React êµ¬ì¡°)
const extractReviews = async (page: Page, maxPages: number = 5): Promise<Review[]> => {
  const reviews: Review[] = [];

  console.log('ğŸ“– ë¦¬ë·° ìˆ˜ì§‘ ì¤‘...');

  // íƒ€ê²Ÿ ë¦¬ë·° ìˆ˜
  const targetReviews = maxPages * 20;
  let noNewDataCount = 0;

  for (let attempt = 0; attempt < maxPages * 5 && reviews.length < targetReviews && noNewDataCount < 3; attempt++) {
    // page.evaluateë¡œ ì§ì ‘ DOMì—ì„œ ë¦¬ë·° ë°ì´í„° ì¶”ì¶œ
    const extractedReviews = await page.evaluate(() => {
      const reviewData: any[] = [];

      // ëª¨ë“  ìš”ì†Œ ì¤‘ ë¦¬ë·°ì²˜ëŸ¼ ë³´ì´ëŠ” ê²ƒ ì°¾ê¸°
      // ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° íŠ¹ì§•: ë³„ì  ì´ë¯¸ì§€, ë‚ ì§œ, ê¸´ í…ìŠ¤íŠ¸
      document.querySelectorAll('*').forEach(el => {
        const text = el.textContent || '';
        const html = el.innerHTML || '';

        // ë¦¬ë·° íŠ¹ì§• í™•ì¸
        const hasDate = /\d{4}\.\d{2}\.\d{2}/.test(text);
        const hasRating = text.includes('ì ') || html.includes('star') || html.includes('rating');
        const isReasonableLength = text.length > 30 && text.length < 3000;
        const className = (el.className || '').toString().toLowerCase();

        // ë¦¬ë·° ì»¨í…Œì´ë„ˆë¡œ ë³´ì´ëŠ” ìš”ì†Œ
        if (hasDate && isReasonableLength && !el.querySelector('article, section, main')) {
          // ì´ë¯¸ ì¶”ì¶œí•œ ë‚´ìš©ê³¼ ì¤‘ë³µ í™•ì¸
          const isDup = reviewData.some(r => text.includes(r.content?.substring(0, 50)));
          if (isDup) return;

