// ë¦¬ë·° ëª©ë¡ ì¶”ì¶œ (ì˜¬ë¦¬ë¸Œì˜ React êµ¬ì¡°)
const extractReviews = async (page: Page, maxPages: number = 5): Promise<Review[]> => {
  const reviews: Review[] = [];

  console.log('ğŸ“– ë¦¬ë·° ìˆ˜ì§‘ ì¤‘...');

  // íƒ€ê²Ÿ ë¦¬ë·° ìˆ˜
  const targetReviews = maxPages * 20;
  let noNewDataCount = 0;

  for (let attempt = 0; attempt < maxPages * 5 && reviews.length < targetReviews && noNewDataCount < 3; attempt++) {
    // page.evaluateë¡œ ì§ì ‘ DOMì—ì„œ ë¦¬ë·° ë°ì´í„° ì¶”ì¶œ
    const extractedReviews = await page.evaluate(() => {
      const reviewData: any[] = [];

      // ëª¨ë“  ìš”ì†Œ ì¤‘ ë¦¬ë·°ì²˜ëŸ¼ ë³´ì´ëŠ” ê²ƒ ì°¾ê¸°
      // ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° íŠ¹ì§•: ë³„ì  ì´ë¯¸ì§€, ë‚ ì§œ, ê¸´ í…ìŠ¤íŠ¸
      document.querySelectorAll('*').forEach(el => {
        const text = el.textContent || '';
        const html = el.innerHTML || '';

        // ë¦¬ë·° íŠ¹ì§• í™•ì¸
        const hasDate = /\d{4}\.\d{2}\.\d{2}/.test(text);
        const hasRating = text.includes('ì ') || html.includes('star') || html.includes('rating');
        const isReasonableLength = text.length > 30 && text.length < 3000;
        const className = (el.className || '').toString().toLowerCase();

        // ë¦¬ë·° ì»¨í…Œì´ë„ˆë¡œ ë³´ì´ëŠ” ìš”ì†Œ
        if (hasDate && isReasonableLength && !el.querySelector('article, section, main')) {
          // ì´ë¯¸ ì¶”ì¶œí•œ ë‚´ìš©ê³¼ ì¤‘ë³µ í™•ì¸
          const isDup = reviewData.some(r => text.includes(r.content?.substring(0, 50)));
          if (isDup) return;

          // ë‚ ì§œ ì¶”ì¶œ
          const dateMatch = text.match(/(\d{4}\.\d{2}\.\d{2})/);
          const date = dateMatch ? dateMatch[1] : '';

          // í…ìŠ¤íŠ¸ ë¼ì¸ ë¶„ë¦¬
          const lines = text.split('\n')
            .map(l => l.trim())
            .filter(l => l.length > 0);

          // ë¦¬ë·° ë‚´ìš© ì°¾ê¸° (ê°€ì¥ ê¸´ ë¼ì¸, ë‹¨ ë‚ ì§œ/ë³„ì  ì œì™¸)
          let content = '';
          for (const line of lines) {
            if (line.length > content.length &&
                line.length > 20 &&
                !line.match(/^\d{4}\.\d{2}\.\d{2}/) &&
                !line.includes('ë„ì›€') &&
                !line.includes('ì‹ ê³ ')) {
              content = line;
            }
          }

          // ì‘ì„±ì ì°¾ê¸° (ë³´í†µ ì§§ì€ ë¬¸ìì—´)
          let reviewer = 'ìµëª…';
          for (const line of lines) {
            if (line.length > 2 && line.length < 20 && !line.match(/\d{4}\.\d{2}/) && line !== content) {
              reviewer = line;
              break;
            }
          }

          // ë³„ì  ì¶”ì¶œ
          let rating = 5;
          const ratingMatch = text.match(/(\d)ì /);
          if (ratingMatch) rating = parseInt(ratingMatch[1]);

          if (content.length > 20) {
            reviewData.push({
              reviewer: reviewer.substring(0, 30),
              rating,
              date,
              content: content.substring(0, 1000)
            });
          }
        }
      });

      // ì¤‘ë³µ ì œê±°
      const unique: any[] = [];
      reviewData.forEach(r => {
        if (!unique.some(u => u.content === r.content)) {
          unique.push(r);
        }
      });

      return unique;
    });

    const prevCount = reviews.length;

    // ì¤‘ë³µ ì œê±°í•˜ë©´ì„œ ì¶”ê°€
    for (const review of extractedReviews) {
      const isDuplicate = reviews.some(r =>
        r.content === review.content ||
        (r.content.length > 50 && review.content.includes(r.content.substring(0, 50)))
      );
      if (!isDuplicate && review.content && review.content.length > 20) {
        reviews.push(review);
      }
    }

    if (reviews.length === prevCount) {
      noNewDataCount++;
    } else {
      noNewDataCount = 0;
      console.log(`   ìˆ˜ì§‘: ${reviews.length}ê°œ`);
    }

    // ìŠ¤í¬ë¡¤ ë‹¤ìš´
    await page.evaluate(() => window.scrollBy(0, 500));
    await randomDelay(1000, 2000);
  }

  console.log(`ğŸ“„ ìˆ˜ì§‘ ì™„ë£Œ: ì´ ${reviews.length}ê°œ`);
  return reviews;
};

