// ëœë¤ ë”œë ˆì´ (ë´‡ íƒì§€ ìš°íšŒ)
const randomDelay = (min: number, max: number): Promise<void> => {
  const delay = Math.floor(Math.random() * (max - min + 1)) + min;
  return new Promise(resolve => setTimeout(resolve, delay));
};

// ë§ˆìš°ìŠ¤ ì›€ì§ì„ ì‹œë®¬ë ˆì´ì…˜
const simulateHumanBehavior = async (page: Page): Promise<void> => {
  const viewport = page.viewportSize();
  if (!viewport) return;

  // ëœë¤ ìœ„ì¹˜ë¡œ ë§ˆìš°ìŠ¤ ì´ë™
  for (let i = 0; i < 3; i++) {
    const x = Math.floor(Math.random() * viewport.width);
    const y = Math.floor(Math.random() * viewport.height);
    await page.mouse.move(x, y, { steps: 10 });
    await randomDelay(100, 300);
  }
};

// ìŠ¤í¬ë¡¤ ì‹œë®¬ë ˆì´ì…˜
const smoothScroll = async (page: Page, distance: number): Promise<void> => {
  await page.evaluate(async (scrollDistance) => {
    await new Promise<void>((resolve) => {
      let totalScrolled = 0;
      const step = 100;
      const timer = setInterval(() => {
        window.scrollBy(0, step);
        totalScrolled += step;
        if (totalScrolled >= scrollDistance) {
          clearInterval(timer);
          resolve();
        }
      }, 50);
    });
  }, distance);
};

