// ë¦¬ë·° ëª©ë¡ ì¶”ì¶œ (ì˜¬ë¦¬ë¸Œì˜ React êµ¬ì¡° - 2025ë…„ ìµœì‹ )
const extractReviews = async (page: Page, maxPages: number = 5): Promise<Review[]> => {
  const reviews: Review[] = [];

  console.log('ğŸ“– ë¦¬ë·° ìˆ˜ì§‘ ì¤‘...');

  // íƒ€ê²Ÿ ë¦¬ë·° ìˆ˜
  const targetReviews = maxPages * 20;
  let noNewDataCount = 0;
  let currentPage = 1;

  // ë¨¼ì € ë¦¬ë·° ì˜ì—­ìœ¼ë¡œ ìŠ¤í¬ë¡¤
  await page.evaluate(() => {
    const reviewSection = document.querySelector('[class*="ReviewList"], [class*="review"], [data-qa-name*="review"]');
    if (reviewSection) {
      reviewSection.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  });
  await randomDelay(2000, 3000);

  for (let attempt = 0; attempt < maxPages * 3 && reviews.length < targetReviews && noNewDataCount < 5; attempt++) {
    // ì˜¬ë¦¬ë¸Œì˜ 2025 êµ¬ì¡°ì— ë§ì¶˜ ë¦¬ë·° ì¶”ì¶œ
    const extractedReviews = await page.evaluate(() => {
      const reviewData: any[] = [];

      // ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° ì•„ì´í…œ ì„ íƒìë“¤ (React í´ë˜ìŠ¤ëª… íŒ¨í„´)
      const reviewSelectors = [
        '[class*="ReviewList"] > div',
        '[class*="review-item"]',
        '[class*="ReviewItem"]',
        'li[class*="review"]',
        '[data-qa-name*="review-item"]',
        // ìŠ¤í¬ë¦°ìƒ·ì—ì„œ ë³¸ êµ¬ì¡° ê¸°ë°˜
        'div[class*="review"] > div',
      ];

      // ë¦¬ë·° ì»¨í…Œì´ë„ˆ ì°¾ê¸°
      let reviewItems: Element[] = [];
      for (const selector of reviewSelectors) {
        const items = document.querySelectorAll(selector);
        if (items.length > 0) {
          reviewItems = Array.from(items);
          break;
        }
      }

      // ëŒ€ì•ˆ: ë‚ ì§œ íŒ¨í„´ì´ ìˆëŠ” ëª¨ë“  ìš”ì†Œ ì¤‘ ë¦¬ë·° êµ¬ì¡° ì°¾ê¸°
      if (reviewItems.length === 0) {
        const allElements = document.querySelectorAll('div, li, article');
        allElements.forEach(el => {
          const text = el.textContent || '';
          const children = el.children.length;

          // ë¦¬ë·° íŠ¹ì„±: ë‚ ì§œ í¬í•¨, ì ì ˆí•œ ê¸¸ì´, ìì‹ì´ ìˆìŒ
          if (/\d{4}\.\d{2}\.\d{2}/.test(text) &&
              text.length > 50 &&
              text.length < 2000 &&
              children > 0 &&
              children < 20) {

            // ë¶€ëª¨ê°€ ì´ë¯¸ ë¦¬ë·° ì•„ì´í…œì´ë©´ ìŠ¤í‚µ
            const parentText = el.parentElement?.textContent || '';
            if (parentText.length < text.length * 1.5) {
              reviewItems.push(el);
            }
          }
        });
      }

      // ê° ë¦¬ë·° ì•„ì´í…œì—ì„œ ë°ì´í„° ì¶”ì¶œ
      reviewItems.forEach((item, index) => {
        try {
          const text = item.textContent || '';

          // ì´ë¯¸ ì¶”ì¶œí•œ ë‚´ìš©ê³¼ ì¤‘ë³µ í™•ì¸ (ì²˜ìŒ 50ì ê¸°ì¤€)
          const first50 = text.substring(0, 50);
          if (reviewData.some(r => r.rawText?.startsWith(first50))) return;

          // ë‚ ì§œ ì¶”ì¶œ
          const dateMatch = text.match(/(\d{4}\.\d{2}\.\d{2})/);
          const date = dateMatch ? dateMatch[1] : '';
          if (!date) return; // ë‚ ì§œ ì—†ìœ¼ë©´ ìŠ¤í‚µ

          // ë³„ì  ì¶”ì¶œ (â˜… ê°œìˆ˜ ë˜ëŠ” ìˆ«ì)
          let rating = 5;
          const starCount = (text.match(/â˜…/g) || []).length;
          if (starCount > 0 && starCount <= 5) {
            rating = starCount;
          } else {
            const ratingMatch = text.match(/(\d)ì /);
            if (ratingMatch) rating = parseInt(ratingMatch[1]);
          }

          // í…ìŠ¤íŠ¸ ë¼ì¸ë³„ ë¶„ì„
          const lines = text.split(/[\n\r]+/)
            .map(l => l.trim())
            .filter(l => l.length > 0);

          // ì‘ì„±ì ì°¾ê¸° (ë³´í†µ ì²« ë²ˆì§¸ ì§§ì€ ë¼ì¸)
          let reviewer = 'ìµëª…';
          for (const line of lines.slice(0, 5)) {
            // ì‘ì„±ì íŒ¨í„´: 2-15ì, í•œê¸€/ì˜ë¬¸, ë‚ ì§œ/ë³„ì  ì•„ë‹˜
            if (line.length >= 2 &&
                line.length <= 15 &&
                !line.match(/\d{4}\.\d{2}/) &&
                !line.includes('â˜…') &&
                !line.includes('ì‹ ê³ ') &&
                !line.includes('ë„ì›€')) {
              reviewer = line;
              break;
            }
          }

          // í”¼ë¶€íƒ€ì…/í”¼ë¶€í†¤ ì¶”ì¶œ
          let skinType = '';
          let skinTone = '';
          const skinPatterns = ['ê±´ì„±', 'ì§€ì„±', 'ë³µí•©ì„±', 'ì¤‘ì„±', 'ë¯¼ê°ì„±', 'ìˆ˜ë¶„ë¶€ì¡±ì§€ì„±'];
          const tonePatterns = ['ì¿¨í†¤', 'ì›œí†¤', 'ë‰´íŠ¸ëŸ´'];

          for (const pattern of skinPatterns) {
            if (text.includes(pattern)) {
              skinType = pattern;
              break;
            }
          }
          for (const pattern of tonePatterns) {
            if (text.includes(pattern)) {
              skinTone = pattern;
              break;
            }
          }

          // ë¦¬ë·° ë‚´ìš© ì°¾ê¸° (ê°€ì¥ ê¸´ ì˜ë¯¸ìˆëŠ” í…ìŠ¤íŠ¸)
          let content = '';
          for (const line of lines) {
            // ì œì™¸ íŒ¨í„´
            if (line.match(/^\d{4}\.\d{2}\.\d{2}/) ||
                line.includes('ì‹ ê³ í•˜ê¸°') ||
                line.includes('ë„ì›€ì´ ëì–´ìš”') ||
                line.includes('ê³µìœ í•˜ê¸°') ||
                line.length < 10) continue;

            // ì˜µì…˜ ì •ë³´ ë¼ì¸ ìŠ¤í‚µ ([ì˜µì…˜] ìœ¼ë¡œ ì‹œì‘)
            if (line.startsWith('[ì˜µì…˜]') || line.startsWith('ì˜µì…˜')) continue;

            // ê°€ì¥ ê¸´ ì‹¤ì œ ë¦¬ë·° ë‚´ìš©
            if (line.length > content.length && line.length >= 15) {
              content = line;
            }
          }

          // ë„ì›€ë¨ ìˆ˜ ì¶”ì¶œ
          let helpfulCount = 0;
          const helpfulMatch = text.match(/(\d+)\s*(ëª…|ê°œ)?.*ë„ì›€/);
          if (helpfulMatch) {
            helpfulCount = parseInt(helpfulMatch[1]);
          }

          if (content.length >= 15) {
            reviewData.push({
              reviewer: reviewer.substring(0, 30),
              rating,
              date,
              content: content.substring(0, 1500),
              skinType: skinType || undefined,
              skinTone: skinTone || undefined,
              helpfulCount,
              rawText: text.substring(0, 100) // ì¤‘ë³µ ì²´í¬ìš©
            });
          }
        } catch (e) {
          // ê°œë³„ ë¦¬ë·° íŒŒì‹± ì‹¤íŒ¨ ë¬´ì‹œ
        }
      });

      // rawText ì œê±°í•˜ê³  ì¤‘ë³µ ì œê±°
      const unique: any[] = [];
      reviewData.forEach(r => {
        delete r.rawText;
        if (!unique.some(u => u.content === r.content)) {
          unique.push(r);
        }
      });

      return unique;
    });

    const prevCount = reviews.length;

    // ì¤‘ë³µ ì œê±°í•˜ë©´ì„œ ì¶”ê°€
    for (const review of extractedReviews) {
      const isDuplicate = reviews.some(r =>
        r.content === review.content ||
        (r.content.length > 30 && r.content.substring(0, 30) === review.content.substring(0, 30))
      );
      if (!isDuplicate && review.content && review.content.length >= 15) {
        reviews.push(review);
      }
    }

    if (reviews.length === prevCount) {
      noNewDataCount++;
      console.log(`   í˜ì´ì§€ ${currentPage}: ìƒˆ ë¦¬ë·° ì—†ìŒ (${noNewDataCount}/5)`);
    } else {
      noNewDataCount = 0;
      console.log(`   í˜ì´ì§€ ${currentPage}: ìˆ˜ì§‘ ${reviews.length}ê°œ`);
    }

    // ë‹¤ìŒ í˜ì´ì§€ë¡œ ì´ë™ ì‹œë„
    try {
      // í˜ì´ì§€ë„¤ì´ì…˜ ë²„íŠ¼ ì°¾ê¸°
      const nextClicked = await page.evaluate((pageNum) => {
        // ë‹¤ìŒ í˜ì´ì§€ ë²„íŠ¼ ë˜ëŠ” í˜ì´ì§€ ë²ˆí˜¸ í´ë¦­
        const pageButtons = document.querySelectorAll('[class*="Pagination"] button, [class*="paging"] a, [class*="page"] button');
        for (const btn of Array.from(pageButtons)) {
          const text = btn.textContent?.trim();
          if (text === String(pageNum + 1) || text === 'ë‹¤ìŒ' || text === '>') {
            (btn as HTMLElement).click();
            return true;
          }
        }

        // ë”ë³´ê¸° ë²„íŠ¼
        const moreButton = document.querySelector('[class*="more"], [class*="More"], button:has-text("ë”ë³´ê¸°")');
        if (moreButton) {
          (moreButton as HTMLElement).click();
          return true;
        }

        return false;
      }, currentPage);

      if (nextClicked) {
        currentPage++;
        await randomDelay(2000, 3000);
      } else {
        // ìŠ¤í¬ë¡¤ë¡œ ë” ë¡œë“œ ì‹œë„
        await page.evaluate(() => window.scrollBy(0, 800));
        await randomDelay(1500, 2500);
      }
    } catch (e) {
      // í˜ì´ì§€ ì´ë™ ì‹¤íŒ¨, ìŠ¤í¬ë¡¤ ì‹œë„
      await page.evaluate(() => window.scrollBy(0, 600));
      await randomDelay(1000, 2000);
    }
  }

  console.log(`ğŸ“„ ìˆ˜ì§‘ ì™„ë£Œ: ì´ ${reviews.length}ê°œ`);
  return reviews;
};

// ê°œë³„ ë¦¬ë·° íŒŒì‹±
const parseReviewItem = async (item: any): Promise<Review | null> => {
  try {
    // ì‘ì„±ì
    const reviewer = await item.locator('.id, .user_id, .reviewer_name, [class*="user"]').first().textContent().catch(() => 'ìµëª…');

    // ë³„ì  (star í´ë˜ìŠ¤ë‚˜ width ê¸°ë°˜)
    let rating = 0;
    const starElement = await item.locator('.score_area .point, .star_score, [class*="rating"]').first();
    if (await starElement.count() > 0) {
      const ratingText = await starElement.textContent().catch(() => '');
      rating = parseFloat(ratingText?.replace(/[^0-9.]/g, '') || '0');

      // width ê¸°ë°˜ ë³„ì  ì¶”ì¶œ
      if (rating === 0) {
        const style = await starElement.getAttribute('style').catch(() => '');
        const widthMatch = style?.match(/width:\s*(\d+)%/);
        if (widthMatch) {
          rating = parseInt(widthMatch[1]) / 20; // 100% = 5ì 
        }
      }
    }

    // ë‚ ì§œ
    const date = await item.locator('.date, .review_date, [class*="date"]').first().textContent().catch(() => '') || '';

    // ë‚´ìš©
    const content = await item.locator('.review_cont, .txt_inner, .review_text, [class*="content"]').first().textContent().catch(() => '') || '';

    // í”¼ë¶€ íƒ€ì…/í†¤ (ìˆìœ¼ë©´)
    const skinType = await item.locator('.tag_skin_type, [class*="skin"]').first().textContent().catch(() => undefined);

    // ë„ì›€ë¨ ìˆ˜
    let helpfulCount = 0;
    const helpfulText = await item.locator('.btn_recom_count, .helpful_count, [class*="like"]').first().textContent().catch(() => '0');
    helpfulCount = parseInt(helpfulText?.replace(/[^0-9]/g, '') || '0');

    // í¬í†  ë¦¬ë·°
    const photoUrls: string[] = [];
    const photos = item.locator('.photo img, .review_photo img');
    const photoCount = await photos.count();
    for (let i = 0; i < photoCount; i++) {
      const src = await photos.nth(i).getAttribute('src');
      if (src) photoUrls.push(src);
    }

    return {
      reviewer: reviewer?.trim() || 'ìµëª…',
      rating,
      date: date.trim(),
      content: content.trim(),
      skinType: skinType?.trim(),
      helpfulCount,
      photoUrls: photoUrls.length > 0 ? photoUrls : undefined,
    };
  } catch (error) {
    console.error('ë¦¬ë·° íŒŒì‹± ì˜¤ë¥˜:', error);
    return null;
  }
};

// ê²°ê³¼ ì €ì¥
const saveResults = (result: CrawlResult, outputDir: string): string => {
  if (!fs.existsSync(outputDir)) {
    fs.mkdirSync(outputDir, { recursive: true });
  }

  const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);
  const filename = `oliveyoung-reviews-${timestamp}.json`;
  const filepath = path.join(outputDir, filename);

  fs.writeFileSync(filepath, JSON.stringify(result, null, 2), 'utf-8');

  // CSVë„ ì €ì¥
  const csvFilename = `oliveyoung-reviews-${timestamp}.csv`;
  const csvPath = path.join(outputDir, csvFilename);
  const csvHeader = 'reviewer,rating,date,content,skinType,helpfulCount\n';
  const csvRows = result.reviews.map(r =>
    `"${r.reviewer}",${r.rating},"${r.date}","${r.content.replace(/"/g, '""')}","${r.skinType || ''}",${r.helpfulCount || 0}`
  ).join('\n');
  fs.writeFileSync(csvPath, csvHeader + csvRows, 'utf-8');

  console.log(`\nâœ… ì €ì¥ ì™„ë£Œ:`);
  console.log(`   JSON: ${filepath}`);
  console.log(`   CSV: ${csvPath}`);

  return filepath;
};

