/**
 * ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° í¬ë¡¤ëŸ¬ - ìµœì¢… ë²„ì „
 *
 * "ë¦¬ë·° ë”ë³´ê¸°" ë²„íŠ¼ í´ë¦­ â†’ ë¦¬ë·° ëª©ë¡ í˜ì´ì§€ â†’ ë¬´í•œ ìŠ¤í¬ë¡¤/í˜ì´ì§€ë„¤ì´ì…˜
 * POST API ìš”ì²­ ìº¡ì²˜
 *
 * ì‚¬ìš©ë²•:
 * npx ts-node scripts/oliveyoung-final-crawler.ts <ìƒí’ˆì½”ë“œ>
 */

import { chromium } from 'playwright';
import * as fs from 'fs';
import * as path from 'path';

interface Review {
  reviewer: string;
  rating: number;
  date: string;
  content: string;
  skinType?: string;
  skinTone?: string;
  helpfulCount?: number;
  photoUrls?: any[];
  option?: string;
}

interface CrawlResult {
  productName: string;
  goodsNo: string;
  totalReviews: number;
  averageRating: number;
  reviews: Review[];
  crawledAt: string;
}

// ìˆ˜ì§‘ëœ ë¦¬ë·°
let capturedReviews: Review[] = [];
let totalReviewCount = 0;
let avgRating = 0;

// ë”œë ˆì´
const delay = (ms: number): Promise<void> =>
  new Promise(resolve => setTimeout(resolve, ms));

// ë¦¬ë·° íŒŒì‹±
const parseReview = (item: any): Review => {
  let reviewer = 'ìµëª…';
  if (item.profileDto?.nickname) {
    reviewer = item.profileDto.nickname;
  }
  let date = item.createdDateTime || '';
  if (date && date.includes('T')) {
    date = date.split('T')[0].replace(/-/g, '.');
  }
  return {
    reviewer,
    rating: item.reviewScore || 5,
    date,
    content: item.content || '',
    skinType: item.profileDto?.skinType,
    skinTone: item.profileDto?.skinTone,
    helpfulCount: item.recommendCount || item.usefulPoint || 0,
    photoUrls: item.photoReviewList || [],
    option: item.goodsDto?.optionNm || '',
  };
};

// API ì‘ë‹µ íŒŒì‹±
const parseApiResponse = (json: any): number => {
  try {
    let reviewList: any[] = [];
    let addedCount = 0;

    if (json?.data && Array.isArray(json.data)) {
      reviewList = json.data;
    }

    if (json?.totalCnt) {
      totalReviewCount = json.totalCnt;
    }

    reviewList.forEach((item: any) => {
      const review = parseReview(item);
      const isDuplicate = capturedReviews.some(r =>
        r.content === review.content && r.date === review.date
      );
      if (!isDuplicate && review.content && review.content.length > 3) {
        capturedReviews.push(review);
        addedCount++;
      }
    });

    return addedCount;
  } catch (e) {
    return 0;
  }
};

// ë©”ì¸ í¬ë¡¤ë§ í•¨ìˆ˜
const crawlOliveyoungReviews = async (
  goodsNo: string,
  outputDir: string = './output'
): Promise<CrawlResult> => {
  console.log('ğŸš€ ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° í¬ë¡¤ëŸ¬ (ìµœì¢…) ì‹œì‘\n');
  console.log(`ğŸ“¦ ìƒí’ˆì½”ë“œ: ${goodsNo}\n`);

  let productName = '';

  const browser = await chromium.launch({
    headless: false,
    args: [
      '--disable-blink-features=AutomationControlled',
      '--no-sandbox',
      '--window-size=1920,1080',
    ],
  });

  try {
    const context = await browser.newContext({
      viewport: { width: 1920, height: 1080 },
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
      locale: 'ko-KR',
      timezoneId: 'Asia/Seoul',
    });

    const page = await context.newPage();

    await page.addInitScript(`
      Object.defineProperty(navigator, 'webdriver', { get: () => undefined });
      window.chrome = { runtime: {} };
    `);

    // Network Response ìº¡ì²˜ (POST ìš”ì²­ë„ í¬í•¨)
    page.on('response', async (response) => {
      const url = response.url();
      const method = response.request().method();

      // ë¦¬ë·° ëª©ë¡ API ìº¡ì²˜ (GETê³¼ POST ëª¨ë‘)
      if (url.includes('/review/api/v2/reviews') &&
          !url.includes('stats') && !url.includes('photo') &&
          !url.includes('summary') && !url.includes('count') && !url.includes('options')) {
        try {
          const json = await response.json();
          const added = parseApiResponse(json);

          if (added > 0) {
            console.log(`ğŸ“¡ [${method}] +${added}ê°œ, ì´ ${capturedReviews.length}/${totalReviewCount}`);
          }
        } catch (e) {}
      }

      // í†µê³„ API
      if (url.includes('/stats')) {
        try {
          const json = await response.json();
          if (json?.data?.totalCount) {
            totalReviewCount = json.data.totalCount;
            console.log(`ğŸ“Š ì´ ë¦¬ë·° ìˆ˜: ${totalReviewCount}`);
          }
          if (json?.data?.averageScore) {
            avgRating = json.data.averageScore;
          }
        } catch (e) {}
      }
    });

    // 1. ìƒí’ˆ í˜ì´ì§€ ë¡œë“œ
    const productUrl = `https://www.oliveyoung.co.kr/store/goods/getGoodsDetail.do?goodsNo=${goodsNo}`;
    console.log('ğŸ“¦ ìƒí’ˆ í˜ì´ì§€ ë¡œë”©...');
    await page.goto(productUrl, { waitUntil: 'domcontentloaded', timeout: 60000 });
    await delay(3000);

    // ìƒí’ˆëª…
    try {
      const title = await page.title();
      if (title) {
        productName = title.split('|')[0].trim();
        console.log(`ğŸ“ ìƒí’ˆëª…: ${productName}`);
      }
    } catch (e) {}

    console.log(`   ì´ˆê¸° ë¡œë”©: ${capturedReviews.length}ê°œ\n`);

    // 2. "ë¦¬ë·° ë”ë³´ê¸°" ë²„íŠ¼ í´ë¦­
    console.log('ğŸ“– ë¦¬ë·° ë”ë³´ê¸° ë²„íŠ¼ í´ë¦­...');

    // ìŠ¤í¬ë¡¤ ë‹¤ìš´ (ë¦¬ë·° ì˜ì—­ìœ¼ë¡œ)
    await page.evaluate(() => window.scrollTo(0, 1500));
    await delay(1000);

    // ë¦¬ë·° ë”ë³´ê¸° ë²„íŠ¼ ì°¾ê¸°
    const moreButtonSelectors = [
      'button:has-text("ë¦¬ë·° ë”ë³´ê¸°")',
      '[class*="review-thumbs"]',
      '[class*="ReviewArea"] button:has-text("ë”ë³´ê¸°")',
      'a:has-text("ë¦¬ë·° ë”ë³´ê¸°")',
    ];

    let moreClicked = false;
    for (const sel of moreButtonSelectors) {
      try {
        const btn = page.locator(sel).first();
        if (await btn.count() > 0 && await btn.isVisible()) {
          await btn.scrollIntoViewIfNeeded();
          await delay(500);
          await btn.click();
          moreClicked = true;
          console.log(`   ë²„íŠ¼ í´ë¦­: ${sel}`);
          break;
        }
      } catch (e) {}
    }

    if (!moreClicked) {
      // ë¦¬ë·° íƒ­ í´ë¦­ ì‹œë„
      console.log('   ë¦¬ë·° íƒ­ í´ë¦­ ì‹œë„...');
      try {
        const reviewTab = page.locator('button:has-text("ë¦¬ë·°"), [class*="tab"]:has-text("ë¦¬ë·°")').first();
        if (await reviewTab.count() > 0) {
          await reviewTab.click();
          await delay(2000);
        }
      } catch (e) {}
    }

    await delay(3000);
    console.log(`   ë¦¬ë·° ë”ë³´ê¸° í›„: ${capturedReviews.length}ê°œ\n`);

    // 3. ë¦¬ë·° ëª©ë¡ì—ì„œ ìŠ¤í¬ë¡¤/í˜ì´ì§€ë„¤ì´ì…˜
    console.log('ğŸ“– ì¶”ê°€ ë¦¬ë·° ë¡œë”©...');

    let noNewCount = 0;
    const maxNoNew = 10;
    let attempts = 0;
    const maxAttempts = 500;  // ìµœëŒ€ 500ë²ˆ ì‹œë„

    while (noNewCount < maxNoNew && attempts < maxAttempts) {
      attempts++;
      const beforeCount = capturedReviews.length;

      // ìŠ¤í¬ë¡¤ ë‹¤ìš´
      await page.evaluate(() => {
        window.scrollBy(0, 800);
      });

      await delay(1000);

      // "ë”ë³´ê¸°", "ë‹¤ìŒ" ë²„íŠ¼ í´ë¦­ ì‹œë„
      try {
        const loadMoreSelectors = [
          'button:has-text("ë”ë³´ê¸°")',
          'button:has-text("ë” ë³´ê¸°")',
          'button:has-text("ë‹¤ìŒ")',
          '[class*="more"] button',
          '[class*="load-more"]',
          '[class*="Pagination"] button:has-text("ë‹¤ìŒ")',
        ];

        for (const sel of loadMoreSelectors) {
          const btn = page.locator(sel).first();
          if (await btn.count() > 0 && await btn.isVisible() && await btn.isEnabled()) {
            await btn.click();
            await delay(2000);
            break;
          }
        }
      } catch (e) {}

      // ê²°ê³¼ ì²´í¬
      if (capturedReviews.length > beforeCount) {
        noNewCount = 0;
      } else {
        noNewCount++;
      }

      // ì§„í–‰ ìƒí™© (50ë²ˆë§ˆë‹¤)
      if (attempts % 50 === 0) {
        const pct = totalReviewCount > 0
          ? Math.round(capturedReviews.length / totalReviewCount * 100)
          : 0;
        console.log(`   ${attempts}íšŒ: ${capturedReviews.length}/${totalReviewCount} (${pct}%)`);
      }

      // ëª©í‘œ ë‹¬ì„±
      if (totalReviewCount > 0 && capturedReviews.length >= totalReviewCount * 0.95) {
        console.log('\n   ê±°ì˜ ëª¨ë“  ë¦¬ë·° ìˆ˜ì§‘ ì™„ë£Œ!');
        break;
      }
    }

    // ê²°ê³¼ ì €ì¥
    const result: CrawlResult = {
      productName: productName || `ìƒí’ˆ ${goodsNo}`,
      goodsNo,
      totalReviews: totalReviewCount || capturedReviews.length,
      averageRating: avgRating,
      reviews: capturedReviews,
      crawledAt: new Date().toISOString(),
    };

    if (!fs.existsSync(outputDir)) {
