ipcMain.handle('analyze', async (event, input: string) => {
  const goodsNo = extractGoodsNo(input);
  if (!goodsNo) {
    return { success: false, error: 'ìœ íš¨í•˜ì§€ ì•Šì€ URL ë˜ëŠ” ìƒí’ˆë²ˆí˜¸ì…ë‹ˆë‹¤.' };
  }

  const sendProgress = (msg: string, percent: number) => {
    mainWindow?.webContents.send('progress', { message: msg, percent });
  };

  try {
    // í¬ë¡¤ë§
    const result = await crawlReviews(goodsNo, sendProgress);

    if (result.reviews.length === 0) {
      return { success: false, error: 'ë¦¬ë·°ë¥¼ ìˆ˜ì§‘í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.' };
    }

    sendProgress('ë¦¬ë·° ë¶„ì„ ì¤‘...', 92);
    const analysis = analyzeReviews(result);

    sendProgress('íŒŒì¼ ì €ì¥ ì¤‘...', 95);
    const outputDir = path.join(app.getPath('documents'), 'ì˜¬ë¦¬ë¸Œì˜ë¶„ì„');
    const paths = saveResults(result, analysis, outputDir);

    sendProgress('ì™„ë£Œ!', 100);

    return {
      success: true,
      data: {
        productName: result.productName,
        totalReviews: result.totalReviews,
        averageRating: result.averageRating,
        analysis,
        paths
      }
    };
  } catch (error: any) {
    return { success: false, error: error.message || 'ì•Œ ìˆ˜ ì—†ëŠ” ì˜¤ë¥˜' };
  }
});

