  const safeName = result.productName.replace(/[<>:"/\\|?*]/g, '').slice(0, 50);

  // JSON
  const jsonPath = path.join(outputDir, `oliveyoung-${result.goodsNumber}-${timestamp}.json`);
  fs.writeFileSync(jsonPath, JSON.stringify(result, null, 2), 'utf-8');

