    `${r.reviewId},${r.rating},"${r.date}","${r.option || ''}","${r.skinType || ''}",${r.helpfulCount},${r.hasPhoto},${r.isRepurchase},"${r.content.replace(/"/g, '""').replace(/\n/g, ' ')}"`
  ).join('\n');
