    const outputDir = path.join(app.getPath('documents'), 'ì˜¬ë¦¬ë¸Œì˜ë¶„ì„');
    const paths = saveResults(result, analysis, outputDir);

    sendProgress('ì™„ë£Œ!', 100);

    return {
