/**
 * ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° í¬ë¡¤ëŸ¬
 * Multi-Sort ì „ëµìœ¼ë¡œ ìµœëŒ€í•œ ë§ì€ ë¦¬ë·° ìˆ˜ì§‘
 */

const { chromium } = require('playwright');
import { Review, CrawlResult, CrawlState, ProgressCallback } from './types';

const delay = (ms: number): Promise<void> => new Promise(resolve => setTimeout(resolve, ms));

// ë¦¬ë·° íŒŒì‹±
function parseReview(item: any): Review {
  return {
    reviewId: item.reviewId,
    content: item.content || '',
    rating: item.reviewScore || 5,
    date: item.createdDateTime || '',
    option: item.goodsDto?.optionName,
    skinType: item.profileDto?.skinType,
    skinTone: item.profileDto?.skinTone,
    helpfulCount: item.recommendCount || 0,
    hasPhoto: item.hasPhoto || false,
    isRepurchase: item.isRepurchase || false,
    photoUrls: item.photoReviewList?.map((p: any) => p.imagePath) || []
  };
}

// URLì—ì„œ ìƒí’ˆë²ˆí˜¸ ì¶”ì¶œ
export function extractGoodsNo(input: string): string | null {
  // ìƒí’ˆë²ˆí˜¸ ì§ì ‘ ì…ë ¥ (A000000235842)
  const directMatch = input.match(/^A\d{12}$/i);
  if (directMatch) return input.toUpperCase();

  // URLì—ì„œ ì¶”ì¶œ
  const urlMatch = input.match(/goodsNo=([A-Z0-9]+)/i);
  if (urlMatch) return urlMatch[1].toUpperCase();

  return null;
}

// íŠ¹ì • ì •ë ¬ë¡œ ë¦¬ë·° ìˆ˜ì§‘
async function crawlWithSort(
  page: any,
  goodsNo: string,
  sortType: string,
  state: CrawlState,
  onProgress: ProgressCallback,
  maxScrolls: number = 4000
): Promise<number> {
  const beforeCount = state.reviews.size;
  let noNewDataCount = 0;
  let scrollCount = 0;

  // API ì‘ë‹µ í•¸ë“¤ëŸ¬
  const handleResponse = async (response: any) => {
    const url = response.url();
    if (url.includes('/review/api/v2/reviews') &&
        !url.includes('stats') &&
        !url.includes('photo-reviews') &&
        !url.includes('count') &&
        !url.includes('options')) {
      try {
        const json = await response.json();
        if (json?.data && Array.isArray(json.data)) {
          for (const item of json.data) {
            if (item.reviewId && !state.reviews.has(item.reviewId)) {
              state.reviews.set(item.reviewId, parseReview(item));
            }
          }
        }
        if (json?.totalCnt) state.totalCount = json.totalCnt;
      } catch (e) {}
    }

    if (url.includes('/stats')) {
      try {
        const json = await response.json();
        if (json?.data) {
