    .map(([keyword, count]) => ({
      keyword,
      count,
      percentage: Math.round((count / totalReviews) * 1000) / 10,
      category: findKeywordCategory(keyword, false)
    }))
    .sort((a, b) => b.count - a.count)
    .slice(0, 15);

  return { positive, negative };
}

