    },
    smell: {
      opportunity: 'í–¥ ê°œì„  ë˜ëŠ” ë¬´í–¥ ì˜µì…˜',
      action: 'ë¬´í–¥ ë¼ì¸ ì¶œì‹œ, ì²œì—° í–¥ë£Œ ì‚¬ìš©'
    },
    price: {
      opportunity: 'ê°€ì„±ë¹„ ë¼ì¸ ë˜ëŠ” ìš©ëŸ‰ ë‹¤ì–‘í™”',
      action: 'ì†Œìš©ëŸ‰ ì²´í—˜íŒ©, ëŒ€ìš©ëŸ‰ ë¦¬í•„íŒ© ì¶œì‹œ'
    },
    usability: {
      opportunity: 'ì‚¬ìš©ë²• ë‹¨ìˆœí™”, í¸ì˜ì„± ê°œì„ ',
      action: 'ì‚¬ìš©ë²• ê°€ì´ë“œ ì˜ìƒ, íŒ¨í‚¤ì§€ UX ê°œì„ '
    }
