/**
 * ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° ë¶„ì„ê¸° - Electron ë©”ì¸ í”„ë¡œì„¸ìŠ¤
 */

const { app, BrowserWindow, ipcMain, shell } = require('electron');
const path = require('path');

// lib ëª¨ë“ˆ import
import { crawlReviews, extractGoodsNo } from './lib/crawler';
import { analyzeReviews, analyzeImprovementOpportunities } from './lib/analyzer';
import { generateMarkdownReport, saveResults } from './lib/reporter';

let mainWindow: any = null;

// ìœˆë„ìš° ìƒì„±
function createWindow(): void {
  mainWindow = new BrowserWindow({
    width: 900,
    height: 700,
    minWidth: 600,
    minHeight: 500,
    title: 'ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° ë¶„ì„ê¸°',
    webPreferences: {
      nodeIntegration: true,
      contextIsolation: false
    },
    autoHideMenuBar: true
  });

  mainWindow.loadFile(path.join(__dirname, 'index.html'));

  mainWindow.on('closed', () => {
    mainWindow = null;
  });
}

app.whenReady().then(createWindow);

app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
