}

// í•œ ì¤„ ìš”ì•½ ìƒì„±
function generateOneLiner(avgRating: number, keywords: BasicAnalysis['keywords']): string {
  const topStrength = keywords.positive[0] ? formatKeyword(keywords.positive[0].keyword) : 'ë§Œì¡±ë„';
