  try {
    // 1. í¬ë¡¤ë§
    const crawlResult = await crawlReviews(goodsNo, sendProgress);

    if (crawlResult.reviews.length === 0) {
      return { success: false, error: 'ë¦¬ë·°ë¥¼ ìˆ˜ì§‘í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.' };
    }

    // 2. ë¶„ì„
    sendProgress('ë¦¬ë·° ë¶„ì„ ì¤‘...', 92);
    const analysis = analyzeReviews(crawlResult);
    const improvements = analyzeImprovementOpportunities(crawlResult, analysis);

    // 3. ì €ì¥
    sendProgress('íŒŒì¼ ì €ì¥ ì¤‘...', 95);
    const outputDir = path.join(app.getPath('documents'), 'ì˜¬ë¦¬ë¸Œì˜ë¶„ì„');
    const { jsonPath, csvPath } = saveResults(crawlResult, outputDir);
    const reportPath = generateMarkdownReport({
      crawlResult,
      analysis,
      improvements,
      outputDir
