  // ê¸ì • - usability
  'ê°„í¸': 'convenient', 'ë…¸ì›Œì‹œ': 'no-wash', 'ë¹ ë¥¸': 'fast', 'ê°„ë‹¨': 'simple',
  'í¸ë¦¬': 'easy', 'ì‰¬ìš´': 'easy', '10ì´ˆ': '10-sec',
  // ê¸ì • - satisfaction
  'ì¬êµ¬ë§¤': 'repurchase', 'ì¶”ì²œ': 'recommend', 'ëŒ€ë€': 'best-seller',
  'ì¸ìƒí…œ': 'HG product', 'ìµœê³ ': 'best', 'ë§Œì¡±': 'satisfied', 'ì¢‹ì•„': 'love it',
  // ê¸ì • - makeup
  'í™”ì˜ë¨¹': 'makeup-friendly', 'ë°€ì°©': 'adherent', 'ì§€ì†ë ¥': 'long-lasting',
  'í™”ì¥': 'makeup', 'ë©”ì´í¬ì—…': 'makeup',
  // ê¸ì • - skincare
  'í”¼ë¶€ê²°': 'skin texture', 'ëª¨ê³µ': 'pores', 'ê°ì§ˆ': 'dead skin',
  'ë§‘ì•„': 'clear', 'í™”ì‚¬': 'radiant', 'ê¹¨ë—': 'clean',
  // ë¶€ì • - effect
