      const npmPath = process.platform === 'win32' ? 'npx.cmd' : 'npx';
      execSync(`${npmPath} playwright install chromium`, {
        stdio: 'inherit',
        env: { ...process.env, PLAYWRIGHT_BROWSERS_PATH: '0' }
      });

      browserInstalled = true;
      dialog.showMessageBoxSync({
        type: 'info',
        title: 'ì„¤ì¹˜ ì™„ë£Œ (Installation Complete)',
        message: 'ë¸Œë¼ìš°ì € ì„¤ì¹˜ê°€ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤!\n(Browser installation complete!)'
      });
      resolve(true);
    } catch (error: any) {
      dialog.showErrorBox(
        'ì„¤ì¹˜ ì‹¤íŒ¨ (Installation Failed)',
        `ë¸Œë¼ìš°ì € ì„¤ì¹˜ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.\n(Browser installation failed.)\n\nìˆ˜ë™ ì„¤ì¹˜: npx playwright install chromium\n\n${error.message}`
      );
      resolve(false);
    }
  });
}

