function createWindow(): void {
  mainWindow = new BrowserWindow({
    width: 900,
    height: 700,
    minWidth: 600,
    minHeight: 500,
    title: 'ì˜¬ë¦¬ë¸Œì˜ ë¦¬ë·° ë¶„ì„ê¸°',
    webPreferences: {
      nodeIntegration: true,
